import { ref } from 'vue'

export function usePortfolioData(type) {
  const works = ref([
    {
      id: 1,
      title: `${type} 作品示例 1`,
      type: type,
      cover: 'https://picsum.photos/600/400?random=1',
      author: {
        name: '张小明',
        avatar: 'https://picsum.photos/32/32?random=1'
      },
      views: 1234,
      likes: 89,
      isVideo: ['video', 'animation'].includes(type),
      duration: '2:35'
    },
    {
      id: 2,
      title: `${type} 作品示例 2`,
      type: type,
      cover: 'https://picsum.photos/600/400?random=2',
      author: {
        name: '李晓华',
        avatar: 'https://picsum.photos/32/32?random=2'
      },
      views: 856,
      likes: 67,
      isVideo: ['video', 'animation'].includes(type),
      duration: '1:45'
    },
    // ... 可以添加更多示例数据
  ])

  return {
    works
  }
}
